mapper.insert:
INSERT INTO REPLY (

		REPLYNUM
	  , BOARDNUM
	  , ID
	  , TEXT

	) values(

		REPLY_SEQ.NEXTVAL
	  , #{boardnum}
	  , #{id}
	  , #{text}

	)

mapper.delete:
DELETE FROM REPLY
 WHERE REPLYNUM = #{replynum}
   AND ID = #{id}

mapper.update:
UPDATE REPLY
   SET TEXT = #{text}
 WHERE REPLYNUM = #{replynum}
   AND ID = #{id}
